// import listVeiculos from "../../services/api";
import { useEffect, useState } from "react";
import api from "../../services/api";

import MarcadorImg from "../../assets/";

import "./index.css";

export default function Main() {
  const [carros, setCarros] = useState({});
  const [card, setCard] = useState([]);
  let arrayCard = [];

  api.then((e) => {
    setCarros(e);
  });
  useEffect(() => {
    handleCards();
  }, []);

  function handleCards() {
    if (Object.values(carros).length == 0) {
      console.log("Aguarde");
    } else {
      let sectionList = document.querySelector("#lista-de-veiculos");
      console.log(sectionList);
      for (let i = 0; i <= Object.values(carros).length - 1; i++) {
        sectionList.innerHTML +=
          '<div class="card"> <div class="dados"> <h3 class="title-card">{carros[i].marca}</h3> <p class="veiculo">{carros[i].veiculo} </p> <p class="ano">{carros[i].ano}</p> </div> <div className="fav"> <img src="../../assets/marcador.png" alt="icone de marcação" /> </div> </div>';
      }
    }
  }

  return (
    <div className="container-main">
      <div className="container-section">
        <div className="lista">
          <p className="title-section">Lista de veículos</p>
          <button onClick={handleCards}>Atualizar Lista</button>
        </div>

        <section id="lista-de-veiculos"> </section>
      </div>

      <div className="container-section">
        <p className="title-section">Detalhes</p>
        <section id="detalhes">Lorem Ipsum</section>
      </div>
    </div>
  );
}
