import { useState } from "react";
import "./index.css";

export default function PopUp(props) {
  const [popup, setPopup] = useState(true)

 function clpopup(){
  setPopup(props.open)
 }

  return (
    <div className="container-popup" style={{
      display: popup ? 'flex' : 'none'
    }}>
      <div className="popup">
        <p>{props.texto}</p>
        <button onClick={e => setPopup(false)}>OK</button>
      </div>
    </div>
  );
}
