import { useEffect, useState } from "react";
import api from "../../services/api";

import "./index.css";

export default function Main() {
  const [carros, setCarros] = useState({});

  // alterar os valores no card detalhes
  const [veiculo, setVeiculo] = useState('');
  const [marca, setMarca] = useState('');
  const [ano, setAno] = useState('');
  const [descricao, setDescricao] = useState('');

  api.then((e) => {
    setCarros(e);
  });

  useEffect(() => {
    handleCards();
    handleDetails();
  });

  function handleCards() {
    if (Object.values(carros).length === 0) {
      console.log("Ainda n√£o pegou os valores da API, tente Atualizar a Lista");
    } else {
      let sectionList = document.querySelector("#lista-de-veiculos");
      sectionList.innerHTML = "";
      for (let i = 0; i <= Object.values(carros).length - 1; i++) {
        sectionList.innerHTML +=
          '<div class="card"> <div class="dados"> <h3 class="title-card">' +
          carros[i].marca +
          ' </h3> <p class="veiculo">' +
          carros[i].veiculo +
          ' </p> <p class="ano">' +
          carros[i].ano +
          '</p> </div> <div class="fav"> <div class="img"> </div </div> </div>';
      }
    }
  }

  function handleDetails() {
    let cards = document.querySelectorAll(".card");
    let dadosCard;

    cards.forEach(function (card) {
      card.addEventListener("click", () => {
        let recebeDadosCard = card.innerText.replaceAll("\n", "-");
        dadosCard = recebeDadosCard.replaceAll("--", "-");

        for (let i = 0; i <= Object.values(carros).length - 1; i++) {
          let idVeiculo =
            carros[i].marca + "-" + carros[i].veiculo + "-" + carros[i].ano;

          if (dadosCard.toLowerCase() === idVeiculo.toLowerCase()) {
            setVeiculo()
            setMarca()
            setAno()
            setDescricao()
          }
        }
      });
    });
  }

  return (
    <div className="container-main">
      <div className="container-section">
        <div className="lista">
          <p className="title-section">Lista de ve√≠culos</p>
          <button onClick={handleCards}>Atualizar Lista</button>
        </div>

        <section id="lista-de-veiculos"> </section>
      </div>

      <div className="container-section details">
        <p className="title-section">Detalhes</p>
        <section id="detalhes">
          <h2>Nome do veiculo</h2>
          <div className="marca-ano">
            <div className="marca">
              <label>Marca</label>
              <p>Marca do veiculo</p>
            </div>
            <div className="ano">
              <label>Ano</label>
              <p>Ano do veiculo</p>
            </div>
          </div>
          <div className="descricao">
            <p>Descri√ß√£o do veiculo</p>
          </div>
          <button className="editar">
            EDITAR
          </button>
        </section>
      </div>
    </div>
  );
}
