import { useState } from "react";
import "./index.css";

export default function PopUp(props) {
  const [popup, setPopup] = useState(true)

  function handlePop(e){
    setPopup(true)
  }


  return (
    <div onChange={e => setPopup(props.open)} className="container-popup" style={{
      display: props.open ? 'none' : 'flex'
    }}>
      <div className="popup">
        <p>{props.texto}</p>
        <button onClick={handlePop}>OK</button>
      </div>
    </div>
  );
}
