import { useEffect, useState } from "react";
import api from "../../services/api";

import "./index.css";

export default function Main() {
  const [carros, setCarros] = useState({});
  const [dadosCarro, setDadosCarro] = useState("");
  var handleDadosCarro = "";

  api.then((e) => {
    setCarros(e);
  });
  useEffect(() => {
    handleCards();
  });

  function handleCards() {
    if (Object.values(carros).length === 0) {
      console.log("Ainda não pegou os valores da API, tente Atualizar a Lista");
    } else {
      let sectionList = document.querySelector("#lista-de-veiculos");
      sectionList.innerHTML = "";
      for (let i = 0; i <= Object.values(carros).length - 1; i++) {
        sectionList.innerHTML += '<div class="card" onclick="(' + handleDetails + ')(this)"> <div class="dados"> <h3 class="title-card">' + carros[i].marca + ' </h3> <p class="veiculo">' + carros[i].veiculo + ' </p> <p class="ano">' + carros[i].ano + '</p> </div> <div class="fav"> <div class="img"> </div </div> </div>';
      }
    }
  }

  function handleDetails(e) {
    let dadosSemTratamento = e.innerText.replaceAll("\n", "-");
    let marcaVeiculoAno = dadosSemTratamento.replaceAll("--", "-");
    handleDadosCarro = marcaVeiculoAno;
    console.log(handleDadosCarro)
  }

  function handleDadosCarroTratar() {
    console.log("Entrou na função");
    if (handleDadosCarro) {
      console.log("Ta vazio");
    } else {
      console.log('aaa')
      // let sectionDetails = document.querySelector("#detalhes");
      // sectionDetails.innerHTML = "";
      // for (let i = 0; i <= Object.values(carros).length - 1; i++) {
      //   sectionDetails.innerHTML +=
      //     '<h3>' +
      //     carros[i].marca +
      //     " </h3> <p>" +
      //     carros[i].veiculo +
      //     " </p> <p>" +
      //     carros[i].ano +
      //     "</p> </div>";
      // }

    }
  }

  return (
    <div className="container-main">
      <div className="container-section">
        <div className="lista">
          <p className="title-section">Lista de veículos</p>
          <button onClick={handleCards}>Atualizar Lista</button>
        </div>

        <section id="lista-de-veiculos"> </section>
      </div>

      <div className="container-section">
        <p className="title-section">Detalhes</p>
        <section id="detalhes"> </section>
      </div>
    </div>
  );
}
