import { useState } from "react";
import "./index.css";

export default function Modal(props) {
  const [boolPopUp, setboolPopUp] = useState(true);

  function handlerSubmit(e) {
    e.preventDefault();

    setBool(false);
  }

  function handlePop() {
    let form = document.querySelector("form");
    let popUp = document.querySelector(".container-popup");
    popUp.style.display = "none";
    form.submit();
  }

  return (
    <div
      className="maxLayout"
      style={{
        display: props.open ? "flex" : "none",
      }}
    >
      <div className="modalLayout">
        <h1>Novo Veículo</h1>
        <form onClick={handlerSubmit} action="/veiculos" method="POST">
          <div className="rowOne">
            <div className="column">
              <div className="label-input">
                {/* <label htmlFor="veiculo">Veículo</label> */}
                <input
                  type="text"
                  id="veiculo"
                  name="veiculo"
                  placeholder="Veículo"
                />
              </div>

              <div className="label-input">
                {/* <label htmlFor="ano">Ano</label> */}
                <input type="number" id="ano" name="ano" placeholder="Ano" />
              </div>
            </div>

            <div className="column">
              <div className="label-input">
                {/* <label htmlFor="marca">Marca</label> */}
                <input
                  type="text"
                  id="marca"
                  name="marca"
                  placeholder="Marca"
                />
              </div>

              <div class="toggle">
                <input type="checkbox" id="vendido" name="vendido" />
                <label htmlFor="vendido" id="vendidoLabel"></label>
                <label htmlFor="vendido">Vendido</label>
              </div>
            </div>
          </div>

          <div className="label-input">
            <label htmlFor="descricao">Descrição</label>
            <textarea name="descricao" id="descricao"></textarea>
          </div>

          <div className="buttons">
            <button type="submit">ADD</button>
            <button id="sair">FECHAR</button>
          </div>
        </form>

        <div
          className="container-popup"
          style={{
            display: boolPopUp ? "none" : "flex",
          }}
        >
          <div className="popup">
            <p>{props.texto}</p>
            <button onClick={handlePop}>OK</button>
          </div>
        </div>
      </div>
    </div>
  );
}
