import { useEffect, useState } from "react";
import lupaImg from "../../assets/lupa.png";

import "./index.css";

export default function Buscar() {
  const [value, setValue] = useState("");

  useEffect(() => {
    var campoFiltro = document.querySelector("#buscar");

    campoFiltro.addEventListener("input", function () {
      var cards = document.querySelectorAll(".card");
      console.log(cards)

      if (this.value.length > 0) {
        for (var i = 0; i < cards.length; i++) {
          var curso = cards[i];
          var nomeCurso = curso.querySelector(".nomeCurso");
          var nome = nomeCurso.textContent;
          var expressao = new RegExp(this.value, "i");

          if (!expressao.test(nome)) {
            curso.classList.add("invisivel");
          } else {
            curso.classList.remove("invisivel");
          }
        }
      } else {
        for (var i = 0; i < cards.length; i++) {
          var curso = cards[i];
          curso.classList.remove("invisivel");
        }
      }
    });
  });

  return (
    <>
      <input type="checkbox" id="checkbox" />
      <input
        type="text"
        name="buscar"
        id="buscar"
        placeholder="BUSCA por um veÃ­culo"
        value={value}
        onChange={(e) => setValue(e.target.value)}
      />
      <label id="labelCheck" htmlFor="checkbox">
        <img src={lupaImg} alt="imagem de lupa de pesquisa" className="lupa" />
      </label>
    </>
  );
}
